import React from 'react';
import styled from 'styled-components'
import Post from './components/Post/Post';

const MainContainer = styled.div`
  display: flex;
  justify-content: center;
  flex-direction: column;
  align-items: center;
`

class App extends React.Component {
  state = {
    pessoas: [
      {
        nomeUsuario: 'paulinha',
        fotoUsuario: 'https://picsum.photos/50/50',
        fotoPost: 'https://picsum.photos/200/150'
      },

      {
        nomeUsuario: 'cláudia',
        fotoUsuario: 'https://picsum.photos/40/50',
        fotoPost: 'https://picsum.photos/190/150'
      },

      { 
      nomeUsuario: 'mayara',
      fotoUsuario: 'https://picsum.photos/30/50',
      fotoPost: 'https://picsum.photos/180/150'
      }
    ]       
  };

  render() {

    const listaDeComponentes = this.state.pessoas.map((pessoa) => {
      // Porém, cada pessoa não é mais uma string somente, mas sim um objeto
      // com duas propriedades. Por isso, no momento de colocá-la na tela
      // precisamos explicitar qual das propriedades queremos mostrar em
      // cada lugar.
      return (
        <p>
          {pessoa.nomeUsuario} - {pessoa.fotoUsuario} - {pessoa.fotoPost}
        </p>
      );
    });      

return (
  <div>{listaDeComponentes}</div>
);
}
}

export default App;
