        valorInputEmail: "",
    };


adicionaDados = () => {

    const dadosEtapa1 = {
        nome: this.state.valorInputNome,
        idade: this.state.valorInputIdade,
        email: this.state.valorInputEmail,
    }


const novoDadosEtapa1 = [...this.state.dadosEtapa1, this.novoDadosEtapa1];

};

render () {

return (

<div>
    <h1>ETAPA 1 - DADOS GERAIS</h1>
      <div>

        <input
