        <input
          value={this.state.valorInputIdade}
          placeholder={"Idade"}
        />

        <input
        value={this.state.valorInputEmail}
        placeholder={"Telefone"}
        />

      </div>

</div>
        );
    }

}
  
  export default Etapa1
